import * as THREE from 'three';
import {OrbitControls} from "../node_modules/three/examples/jsm/controls/OrbitControls";
//import "regenerator-runtime/runtime.js";

const LINK_THRESHOLD = 0.85;
const NODE_RADIUS = 2;
const NODE_SEGMENTS = 20;
const NODE_RINGS = 50;

const NODE_BASE_COLOR = 0xCCCC22;

const scene = new THREE.Scene();
const renderer = new THREE.WebGLRenderer();
document.body.appendChild( renderer.domElement );

const camera = new THREE.PerspectiveCamera();
const controls = new OrbitControls( camera, renderer.domElement );
controls.update();
controls.autoRotate = false;
const mouse = new THREE.Vector2();
let INTERSECTED;
let nodeNameDiv = document.getElementById('nodeName');
console.log(nodeNameDiv.textContent);
const raycaster = new THREE.Raycaster();
THREE.Cache.enabled = true;
const loader = new THREE.FileLoader();

const allSpheres = [];

const linkMaterial = new THREE.LineBasicMaterial({color: 0xBBBBBB});


init();
animate();




function onWindowResize() {

  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();

  renderer.setSize( window.innerWidth, window.innerHeight );

}

function onDocumentMouseMove( event ) {
  event.preventDefault();
  mouse.x = ( event.clientX / window.innerWidth ) * 2 - 1;
          mouse.y = - ( event.clientY / window.innerHeight ) * 2 + 1;
  const padding = 15;
  nodeNameDiv.style.top = event.clientY + padding;
  nodeNameDiv.style.left = event.clientX + padding;
}


function init () {

  camera.position.z = 300;
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  scene.add(camera);
  renderer.setSize( window.innerWidth, window.innerHeight );

  scene.add( new THREE.AmbientLight( 0xaaaaaa ) );
  const light = new THREE.DirectionalLight( 0xffffff, 1 );
  light.position.set(100,100,100);
  scene.add(light);

  loadAndDrawBrainModel();

  window.addEventListener( 'resize', onWindowResize );
  document.addEventListener( 'mousemove', onDocumentMouseMove );

}


function animate() {

  requestAnimationFrame( animate );

  // required if controls.enableDamping or controls.autoRotate are set to true
  controls.update();
  hoverDisplayUpdate();

  renderer.render( scene, camera );

}

function hoverDisplayUpdate(){
  raycaster.setFromCamera( mouse, camera );
  const intersects = raycaster.intersectObjects( allSpheres );

  emptyIntersected();
  if (intersects.length !== 0) {
    if ( INTERSECTED != intersects[ 0 ].object ){
      INTERSECTED = intersects[0].object;
      INTERSECTED.material.color.set(0xffffff);
      nodeNameDiv.innerHTML = INTERSECTED.name;
    }
  }
}

function emptyIntersected(){
  if (INTERSECTED) INTERSECTED.material.color.set(NODE_BASE_COLOR);
  INTERSECTED = null;
  nodeNameDiv.innerHTML = '';
}

async function loadAndDrawBrainModel(){
    await loadBrainModel();
    //TODO: drawBrainModel
}

async function loadBrainModel(){
    const brainVertices = [];
    loader.load( './cortex_vert.csv',
        function (data){
            console.log(data);
        },
        function (xhr) {console.log( ('Loading brain vertices : ' + xhr.loaded / xhr.total * 100) + '% loaded' )},
        function(err)  {console.error(err)}
    );
}
// /Users/thibault.rolland/Documents/mydata/blender_connectivity_matrix/cortex_coordinates/cortex_tri.csv

function loadNodePositions(){
    //TODO
    loader.load( '',
        function (data){console.log(data);},
        function (xhr) {},
        function(err)  {console.error(err)}
    );
}
